jQuery(document).ready(function($) {
	/*$('main').css('paddingTop', $('nav').outerHeight());*/
	if ($(window).width() > 992) {
		$('.owl-carousel').owlCarousel({
			loop: true,
			items: 4,
			stagePadding: 20,
			margin: 30,
			dots: true,
			nav: true,
			slideBy: 2
		});
	}
	else if ($(window).width() > 767) {
		$('.owl-carousel').owlCarousel({
			loop: true,
			items: 2,
			stagePadding: 10,
			margin: 20,
			dots: true,
			nav: true,
			slideBy: 2
		});
	} else {
		$('.owl-carousel').owlCarousel({
			loop: true,
			items: 2,
			stagePadding: 4,
			margin: 8,
			dots: true,
			nav: true,
			slideBy: 2
		});
	}
});